class Fruits {
   public $fruits = 'grape ';
   public function output_fruits_name() { return 'lemon '; }

   function test() {
      echo $this->fruits;
      echo $this->output_fruits_name() ;
   }
}

$obj = new Fruits();
$obj->test();

class Fruits {
   const FRUITS = 'apple ';
   public static $fruits = 'grape ';

   function test() {
      echo self::FRUITS;
      echo self::$fruits;
      echo self::output_fruits_name() ;
   }
}

$obj = new Fruits();
$obj->test();

